mapper.select:
SELECT CMP_CODE , DOC_NO , USER_CODE , 'lsh' USER_NAME , (SELECT CODE_NAME FROM CODE_CHILD A WHERE PARENT_CODE='DOC001' AND CODE_VALUE=DOC_TYPE) DOC_TYPE , (SELECT CODE_NAME FROM CODE_CHILD A WHERE PARENT_CODE='DOC002' AND CODE_VALUE=DOC_STATUS) DOC_STATUS , DATE_FORMAT(REQUEST_YMD,'%Y-%m-%d') REQUEST_YMD , DATE_FORMAT(SIGN_YMD,'%Y-%m-%d') SIGN_YMD FROM PM_DOCUMENT WHERE CMP_CODE = #{cmpCode} AND USER_CODE = #{userCode}
AND DOC_STATUS = #{docStatus}
AND DATE_FORMAT(REQUEST_YMD,'%Y%m%d') BETWEEN REPLACE(#{startDay},'-','') AND REPLACE(#{endDay},'-','')
AND DATE_FORMAT(SIGN_YMD,'%Y%m%d') BETWEEN REPLACE(#{startDay},'-','') AND REPLACE(#{endDay},'-','')
ORDER BY DOC_NO DESC

SELECT PS.CMP_CODE , PS.DOC_NO , PS.USER_CODE , 'lsh' USER_NAME , (SELECT CODE_NAME FROM CODE_CHILD A WHERE PARENT_CODE='DOC001' AND CODE_VALUE=PD.DOC_TYPE) DOC_TYPE , (SELECT CODE_NAME FROM CODE_CHILD A WHERE PARENT_CODE='DOC002' AND CODE_VALUE=PD.DOC_STATUS) DOC_STATUS , DATE_FORMAT(PD.REQUEST_YMD,'%Y-%m-%d') REQUEST_YMD , DATE_FORMAT(PD.SIGN_YMD,'%Y-%m-%d') SIGN_YMD FROM PM_DOCUMENT_SIGN PS LEFT OUTER JOIN PM_DOCUMENT PD ON PS.CMP_CODE = PD.CMP_CODE AND PS.DOC_NO = PD.DOC_NO WHERE PS.CMP_CODE = #{cmpCode} AND PS.SIGN_USER = #{userCode}
AND PS.DOC_STATUS = #{docStatus}
AND DATE_FORMAT(REQUEST_YMD,'%Y%m%d') BETWEEN REPLACE(#{startDay},'-','') AND REPLACE(#{endDay},'-','')
AND DATE_FORMAT(SIGN_YMD,'%Y%m%d') BETWEEN REPLACE(#{startDay},'-','') AND REPLACE(#{endDay},'-','')
ORDER BY PS.DOC_NO DESC

mapper.insert:
INSERT INTO PM_DOCUMENT (
	  CMP_CODE
	, DOC_NO
	, USER_CODE
	, DOC_TYPE
	, DOC_STATUS
	, REQUEST_YMD
	, SIGN_YMD
	, RETURN_CAUSE
) VALUES (
	  #{cmpCode}
	, #{docNo}
	, #{userCode}
	, #{docType}
	, #{docStatus}
	, NOW()
	, NULL
	, ''
)

INSERT INTO PM_DOCUMENT_LEAVE (
	  CMP_CODE
	, DOC_NO
	, LEAVE_TYPE
	, DAY_TYPE
	, START_DAY
	, END_DAY
	, LEAVE_CNT
	, REMARK
) VALUES (
	  #{cmpCode}
	, #{docNo}
	, #{leaveType}
	, #{dayType}
	, REPLACE(#{startDay},'-','')
	, REPLACE(#{endDay},'-','')					
	, #{leaveCnt}
	, #{remark}
)

INSERT INTO PM_DOCUMENT_SIGN (
	  CMP_CODE
	, DOC_NO
	, SIGN_SEQ
	, SIGN_USER
	, SIGN_YMD
	, DOC_STATUS			
) VALUES (
	  #{cmpCode}
	, #{docNo}
	, (SELECT IFNULL(MAX(SIGN_SEQ),0)+1
	     FROM PM_DOCUMENT_SIGN A 
	    WHERE CMP_CODE = #{cmpCode} 
	      AND DOC_NO   = #{docNo})
	, #{signUser}
	, NULL
	, '0001'			
)
