mapper.select:
SELECT PD.DOC_NO , PD.USER_CODE , PU.USER_NAME , (SELECT CHILD_NAME FROM CODE_CHILD A WHERE PARENT_CODE='DOC001' AND CHILD_CODE=PD.DOC_TYPE) DOC_TYPE , (SELECT CHILD_NAME FROM CODE_CHILD A WHERE PARENT_CODE='DOC002' AND CHILD_CODE=PD.DOC_STATUS) DOC_STATUS , DATE_FORMAT(PD.REQUEST_YMD,'%Y-%m-%d') REQUEST_YMD , DATE_FORMAT(PD.SIGN_YMD,'%Y-%m-%d') SIGN_YMD FROM PM_DOCUMENT PD LEFT OUTER JOIN PM_USER PU ON PD.CMP_CODE = PU.CMP_CODE AND PD.USER_CODE = PU.USER_CODE WHERE PD.CMP_CODE = #{cmpCode} AND PD.USER_CODE = #{userCode}
AND PD.DOC_STATUS = #{docStatus}
AND DATE_FORMAT(PD.REQUEST_YMD,'%Y%m%d') BETWEEN REPLACE(#{startDay},'-','') AND REPLACE(#{endDay},'-','')
AND DATE_FORMAT(PD.SIGN_YMD,'%Y%m%d') BETWEEN REPLACE(#{startDay},'-','') AND REPLACE(#{endDay},'-','')
ORDER BY PD.DOC_NO DESC

SELECT PS.DOC_NO , PD.USER_CODE , (SELECT USER_NAME FROM PM_USER A WHERE CMP_CODE=PD.CMP_CODE AND USER_CODE=PD.USER_CODE) USER_NAME , (SELECT CHILD_NAME FROM CODE_CHILD A WHERE PARENT_CODE='DOC001' AND CHILD_CODE=PD.DOC_TYPE) DOC_TYPE , (SELECT CHILD_NAME FROM CODE_CHILD A WHERE PARENT_CODE='DOC002' AND CHILD_CODE=PD.DOC_STATUS) DOC_STATUS , DATE_FORMAT(PD.REQUEST_YMD,'%Y-%m-%d') REQUEST_YMD , DATE_FORMAT(PD.SIGN_YMD,'%Y-%m-%d') SIGN_YMD FROM PM_DOCUMENT_SIGN PS INNER JOIN PM_DOCUMENT PD ON PS.CMP_CODE = PD.CMP_CODE AND PS.DOC_NO = PD.DOC_NO WHERE PS.CMP_CODE = #{cmpCode} AND PS.SIGN_USER = #{userCode}
AND PS.DOC_STATUS = #{docStatus}
AND DATE_FORMAT(REQUEST_YMD,'%Y%m%d') BETWEEN REPLACE(#{startDay},'-','') AND REPLACE(#{endDay},'-','')
AND DATE_FORMAT(SIGN_YMD,'%Y%m%d') BETWEEN REPLACE(#{startDay},'-','') AND REPLACE(#{endDay},'-','')
ORDER BY PS.DOC_NO DESC

mapper.insert:
INSERT INTO PM_DOCUMENT (
	  CMP_CODE
	, DOC_NO
	, USER_CODE
	, DOC_TYPE
	, DOC_STATUS
	, REQUEST_YMD
	, SIGN_YMD
	, RETURN_CAUSE
) VALUES (
	  #{cmpCode}
	, #{docNo}
	, #{userCode}
	, #{docType}
	, #{docStatus}
	, NOW()
	, NULL
	, ''
)

INSERT INTO PM_DOCUMENT_LEAVE (
	  CMP_CODE
	, DOC_NO
	, LEAVE_TYPE
	, DAY_TYPE
	, START_DAY
	, END_DAY
	, LEAVE_CNT
	, REMARK
) VALUES (
	  #{cmpCode}
	, #{docNo}
	, #{leaveType}
	, #{dayType}
	, REPLACE(#{startDay},'-','')
	, REPLACE(#{endDay},'-','')					
	, #{leaveCnt}
	, #{remark}
)

INSERT INTO PM_DOCUMENT_SIGN (
	  CMP_CODE
	, DOC_NO
	, SIGN_SEQ
	, SIGN_USER
	, SIGN_YMD
	, DOC_STATUS			
) VALUES (
	  #{cmpCode}
	, #{docNo}
	, (SELECT IFNULL(MAX(SIGN_SEQ),0)+1
	     FROM PM_DOCUMENT_SIGN A 
	    WHERE CMP_CODE = #{cmpCode} 
	      AND DOC_NO   = #{docNo})
	, #{signUser}
	, NULL
	, '0001'			
)

mapper.select:
SELECT IFNULL(MAX(PS.AUTOSIGN_YN),'N') AUTOSIGN_YN
  FROM PM_SIGN PS
       LEFT OUTER JOIN PM_USER PU
         ON PS.CMP_CODE  = PU.CMP_CODE
		AND PS.DEPT_CODE = PU.DEPT_CODE
        AND PS.PART_CODE = PU.PART_CODE
 WHERE PS.CMP_CODE  = #{cmpCode}
   AND PS.DOC_TYPE  = #{docType}
   AND PU.USER_CODE = #{userCode}

{
	CALL SP_SIGN_DOCUMENT (
		  #{cmpCode , mode=IN, jdbcType=VARCHAR, javaType=String}
		, #{docNo   , mode=IN, jdbcType=VARCHAR, javaType=String}
		, #{userCode, mode=IN, jdbcType=VARCHAR, javaType=String}
	)
}

mapper.update:
UPDATE PM_DOCUMENT_SIGN
   SET DOC_STATUS = '0003'
 WHERE CMP_CODE  = #{cmpCode}
   AND DOC_NO    = #{docNo}
   AND SIGN_USER = #{userCode};

UPDATE PM_DOCUMENT
   SET DOC_STATUS   = '0003'
   	 , RETURN_CAUSE = #{returnCause}
 WHERE CMP_CODE = #{cmpCode}
   AND DOC_NO   = #{docNo};
