mapper.select:
SELECT USER_CODE
	 , USER_NAME
	 , DATE_FORMAT(ENTER_YMD, '%Y-%m-%d') ENTER_YMD
  FROM PM_USER	
 WHERE CMP_CODE  = #{cmpCode}
   AND USER_CODE = #{userCode}

mapper.insert:
		INSERT INTO PM_USER (
			  CMP_CODE
			, USER_CODE
			, PASSWORD
			, USER_NAME
			, USER_AUTH
			, BIRTH_YMD
			, GENDER
			, MOBILE
			, EMAIL
			, DEPT
			, JOB_POS
			, ENTER_YMD
			, RETIRE_YMD
			, SALT
		) VALUES (
			  #{cmpCode}
			, (
 			  SELECT RN.USER_CODE
				FROM (
				  	  SELECT CMP_CODE
							 , LPAD(FLOOR(RAND() * USER_CODE_RULE), LENGTH(USER_CODE_RULE), '0') USER_CODE
						  FROM PM_COMPANY 
						 WHERE CMP_CODE = #{cmpCode}
					   ) RN LEFT OUTER JOIN PM_USER PU	   
						 ON RN.CMP_CODE  = PU.CMP_CODE
						AND RN.USER_CODE = PU.USER_CODE
				 WHERE PU.USER_CODE IS NULL
			  )
			, #{password}
			, #{userName}
			, #{userAuth}
			, REPLACE(#{birthYmd},'-','')
			, #{gender}
			, #{mobile}
			, #{email}
			, #{dept}
			, #{jobPos}
			, REPLACE(#{enterYmd},'-','')
			, REPLACE(#{retireYmd},'-','')
			, #{salt}
		)
