mapper.select:
SELECT IFNULL(MAX(MAILNO), 0) + 1 CNT
  FROM PM_MAIL
 WHERE CMPCD = #{cmpcd}

mapper.update:
UPDATE PM_MAIL		  
   SET READDATE =  NOW()
 WHERE CMPCD  = #{cmpcd}
   AND USERCD = #{usercd}
   AND MAILNO = #{mailno}

UPDATE PM_MAIL
   SET MAILSTATUS = #{mailStatus}
 WHERE CMPCD = #{cmpcd}
   AND USERCD = #{usercd}


 		AND MAILNO IN

 			#{item}



 		AND MAILNO = #{mailno}

mapper.delete:
DELETE FROM PM_MAIL		    
 WHERE CMPCD = #{cmpcd}


 		AND MAILNO IN

 			#{item}



 		AND MAILNO = #{mailno}
